module.exports = screen;

var exec = require('child_process').exec;

function screen(options){
	this.options = Object.assign({
		screen: 'nodejs'
	}, options);
}

screen.prototype.exec = function(command) {
	var name = this.options.screen;
	
	exec('screen -DmS '+name+' -p 0 -X stuff "'+command+'"');
};