module.exports = commander = require('commander');

commander
    .version('1.0.0')
    .option('-start, --start, start', 'start server')
	.option('-stop, stop', 'stop server')
	.option('-restart, restart', 'restart server')
	.option('-status, status', 'check if the server is running')
	.option('-switch, switch', 'switch to screen ( only if screen is enabled )');